              .pathRadio__title {{$t('signUpPath.inspiration.title')}}
              ul.pathRadio__list
                li.pathRadio__li {{ $t('signUpPath.inspiration.list[0]')}}
                li.pathRadio__li {{ $t('signUpPath.inspiration.list[1]')}}
                li.pathRadio__li {{ $t('signUpPath.inspiration.list[2]')}}
        .app__groupBtns
          .button.signUpPath__btn(@click="back")
            span {{$t('signUpPath.back')}}
          button.button.button_next.signUpPath__btn(type="submit" @click="onSubmit" :disabled="!choice")
            span {{$t('signUpPath.enter')}}


</template>

<script>
  import { mapMutations } from 'vuex';
  import { authRoutesMap } from '../../config';
  import Tooltip from '../../components/Tooltip';

  export default {
    components: {
      Tooltip
    },
    props: ['token'],
    data() {
      return {
        choice: false
      };
    },
    created() {
    },
    methods: {
      ...mapMutations({
        setPath: 'signUp/SET_PATH',
        setLevel: 'signUp/SET_LEVEL'
      }),
      back() {
        this.setLevel(0);
        this.$router.back();
      },
      async onSubmit() {
        if (this.choice) {
          this.setPath(this.choice);
          this.$router.push(authRoutesMap.skills);
        }
      }
    }
  };
</script>

